import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import axios from 'axios';

// Pages (you can create separate components for these)
import HomePage from './pages/HomePage';
import DataSourcesPage from './pages/DataSourcesPage';
import LineagePage from './pages/LineagePage';

function App() {
  const [backendStatus, setBackendStatus] = useState(null);

  // Check if the backend is running (for example, with a health check or a simple API endpoint)
//   useEffect(() => {
//     axios.get('http://localhost:3001/api/status')  // Replace with your backend's actual endpoint
//       .then((response) => {
//         setBackendStatus('Backend is up and running');
//       })
//       .catch((error) => {
//         setBackendStatus('Backend is down');
//       });
//   }, []);

  return (
    <Router>
      <div>
        {/* Navbar */}
        <nav>
          <ul>
            <li><Link to="/">Home</Link></li>
            <li><Link to="/data-sources">Data Sources</Link></li>
            <li><Link to="/lineage">Lineage</Link></li>
          </ul>
        </nav>

        {/* Backend status message */}
        <div>
          <p>{backendStatus ? backendStatus : 'Loading backend status...'}</p>
        </div>

        {/* Page routing */}
        <Switch>
          <Route path="/" exact component={HomePage} />
          <Route path="/data-sources" component={DataSourcesPage} />
          <Route path="/lineage" component={LineagePage} />
        </Switch>
      </div>
    </Router>
  );
}

export default App;
