import React from "react";
import { Graph } from "react-d3-graph";

const LineageGraph = ({ lineageData }) => {
    const graphConfig = {
        automaticRearrangeAfterDropNode: false,
        collapsible: false,
        directed: false,
        focusAnimationDuration: 0.75,
        focusZoom: 1,
        freezeAllDragEvents: false,
        height: 400,
        highlightDegree: 1,
        highlightOpacity: 1,
        linkHighlightBehavior: false,
        maxZoom: 8,
        minZoom: 0.1,
        nodeHighlightBehavior: false,
        panAndZoom: false,
        staticGraph: false,
        staticGraphWithDragAndDrop: false,
        width: 800,
        d3: {
          alphaTarget: 0.05,
          gravity: -100,
          linkLength: 100,
          linkStrength: 1,
          disableLinkForce: false
        },
        node: {
          color: "#d3d3d3",
          fontColor: "black",
          fontSize: 8,
          fontWeight: "normal",
          highlightColor: "SAME",
          highlightFontSize: 8,
          highlightFontWeight: "normal",
          highlightStrokeColor: "SAME",
          highlightStrokeWidth: "SAME",
          labelProperty: "id",
          mouseCursor: "pointer",
          opacity: 1,
          renderLabel: true,
          size: 200,
          strokeColor: "none",
          strokeWidth: 1.5,
          svg: "",
          symbolType: "circle"
        },
        link: {
          color: "#d3d3d3",
          fontColor: "black",
          fontSize: 8,
          fontWeight: "normal",
          highlightColor: "SAME",
          highlightFontSize: 8,
          highlightFontWeight: "normal",
          labelProperty: "label",
          mouseCursor: "pointer",
          opacity: 1,
          renderLabel: false,
          semanticStrokeWidth: false,
          strokeWidth: 1.5,
          markerHeight: 6,
          markerWidth: 6,
          strokeDasharray: 0,
          strokeDashoffset: 0,
          strokeLinecap: "butt"
        }
      };

    //   useEffect(() => {
    //     // Log lineageData to ensure it has the expected structure
    //     console.log("Lineage data:", lineageData);
    //   }, [lineageData]);

    //   // Check if lineageData is in the correct format
    //   if (!lineageData || !lineageData.nodes || !lineageData.links) {
    //     return <div>Error: Invalid lineage data format.</div>;
    //   }




const handleNodeClick = (nodeId) => {
    if (!nodeId) {
        console.error("Invalid node clicked:", nodeId);
        return; // Prevent further actions if nodeId is invalid
    }
    console.log("Node clicked:", nodeId);
    alert(`You clicked on node: ${nodeId}`);  // Example of a simple action
};


    return (
        <div style={{ height: "600px", width: "100%" }}>
            {lineageData ? (
                <Graph
                    id="lineage-graph"
                    data={lineageData}
                    config={graphConfig}
                    onClickNode={(nodeId) => console.log("Node clicked:", nodeId)}
                />
            ) : (
                <p>Loading lineage graph...</p>
            )}
        </div>
    );
};

export default LineageGraph;
