/**
 * (y1 - y2) * x + (x2 - x1) * y + (x1 * y2 - x2 * y1) = 0
 * a = y1 - y2
 * b = -(x2 - x1) = x1 - x2
 * c = -(x1 * y2 - x2 * y1) = x2 * y1 - x1 * y2
 *
 * a * x - b * y = c
 * x = (b * y + c) / a
 * y = (a * x - c) / b
 */
export declare class LinearEquation {
    private readonly a;
    private readonly b;
    private readonly c;
    constructor(x1: number, y1: number, x2: number, y2: number);
    getX(y: number): number | undefined;
    getY(x: number): number | undefined;
}
